    requestCount := 0

    ageToCount := make(map[int]int)
    for i := 0; i < len(ages); i++ {
        ageToCount[ages[i]]++
    }

    uniqueAges := make([]int, 0, len(ageToCount))
    for age, _ := range ageToCount {
        uniqueAges = append(uniqueAges, age)
    }

    sort.Ints(uniqueAges)

    n := len(uniqueAges)
    for i := 0; i < n; i ++ {
        for j := 0; j < n; j++ {
            if sendRequest(uniqueAges[i], uniqueAges[j]) {
                if i == j {
                    requestCount += ageToCount[uniqueAges[i]] * (ageToCount[uniqueAges[i]] - 1) 
                } else {
                    requestCount += (ageToCount[uniqueAges[i]] * ageToCount[uniqueAges[j]])
                }
            }
        }
    }

    return requestCount
}

