      if (ages[x] < 100) {
        upper_y = min(upper_y, 100);
      }

      const int lower_y = ages[x] / 2 + 7;
      ret += static_cast<int>(max(0L,
                                  upper_bound(ages.begin(), ages.end(), upper_y) -
                                  upper_bound(ages.begin(), ages.end(), lower_y) -
                                  (ages[x] <= upper_y && ages[x] >= lower_y ? 1 : 0)));
    }
    return ret;
  }
