// Recursion
// TLE
public int minOperationsRec(int[] nums, int x) {

	int len = nums.length;
	int count = minOperationsHelper(nums, x, 0, len - 1);
	return (count == Integer.MAX_VALUE) ? -1 : count;
}

// O(2^n) O(n)
// Recursion
public int minOperationsHelper(int[] nums, int x, int si, int ei) {

	if (x == 0)
		return 0;

	if (x < 0 || si > ei)
		return Integer.MAX_VALUE;

	int front = minOperationsHelper(nums, x - nums[si], si + 1, ei);
	int rear = minOperationsHelper(nums, x - nums[ei], si, ei - 1);

	int min = Math.min(front, rear);
	return min == Integer.MAX_VALUE ? min : min + 1;
}

// O(n^2) O(n^2)
// Memoization
// TLE
public int minOperationsMemo(int[] nums, int x) {

	int len = nums.length;
	int[][] memo = new int[len][len];
	int count = minOperationsHelper(nums, x, 0, len - 1, memo);
	return (count == Integer.MAX_VALUE) ? -1 : count;
}

// O(n^2) O(n^2)
// Memoization
public int minOperationsHelper(int[] nums, int x, int si, int ei, int[][] memo) {

	if (x == 0)
		return 0;

	if (x < 0 || si > ei)
		return Integer.MAX_VALUE;

	if (memo[si][ei] != 0)
		return memo[si][ei];

	int front = minOperationsHelper(nums, x - nums[si], si + 1, ei, memo);
	int rear = minOperationsHelper(nums, x - nums[ei], si, ei - 1, memo);

	int min = Math.min(front, rear);
	int ans = (min == Integer.MAX_VALUE) ? min : min + 1;
	memo[si][ei] = ans;
	return ans;
}

// O(n) O(n)
// HashMap
public int minOperations1(int[] nums, int x) {

	int len = nums.length, total = 0;
	for (int i = 0; i < len; i++)
		total += nums[i];

	int target = total - x;
	int ans = subArraySumK1(nums, target);
	return (ans == -1) ? -1 : len - ans;
}

// O(n) O(n)
// HashMap
public int subArraySumK1(int[] arr, int K) {

	if (K == 0)
		return 0;

	int len = arr.length, sum = 0, ans = -1;
	HashMap<Integer, Integer> map = new HashMap<>();
	map.put(sum, -1);

	for (int i = 0; i < len; i++) {

		sum += arr[i];
		if (map.containsKey(sum - K)) {
			int idx = map.get(sum - K);
			if (i - idx > ans)
				ans = i - idx;
		}
		map.put(sum, i);
	}

	return ans;
}

