	int len = nums.length, total = 0;
	for (int i = 0; i < len; i++)
		total += nums[i];

	int target = total - x;
	int ans = subArraySum2(nums, target);
	return (ans == -1) ? -1 : len - ans;
}

// O(n^2) O(n)
// Brute Force
public int subArraySum2(int[] nums, int target) {

	int len = nums.length, ans = -1;

	int[] prefix = new int[len];
	prefix[0] = nums[0];
	for (int i = 1; i < len; i++)
		prefix[i] = prefix[i - 1] + nums[i];

	for (int i = 0; i < len; i++) {
		for (int j = i; j < len; j++) {
			int sum = prefix[j] - prefix[i] + nums[i];
			if (sum == target) {
				if (j - i + 1 > ans)
					ans = j - i + 1;
			} else if (sum > target)
				break;
		}
	}

	return ans;
}

