public int minOperations2(int[] nums, int x) {

	int len = nums.length, total = 0;
	for (int i = 0; i < len; i++)
		total += nums[i];

	int target = total - x;
	int ans = subArraySum2(nums, target);
	return (ans == -1) ? -1 : len - ans;
}

// O(n^2) O(n)
// Brute Force
public int subArraySum2(int[] nums, int target) {

	int len = nums.length, ans = -1;

	int[] prefix = new int[len];
	prefix[0] = nums[0];
	for (int i = 1; i < len; i++)
		prefix[i] = prefix[i - 1] + nums[i];

	for (int i = 0; i < len; i++) {
		for (int j = i; j < len; j++) {
			int sum = prefix[j] - prefix[i] + nums[i];
			if (sum == target) {
				if (j - i + 1 > ans)
					ans = j - i + 1;
			} else if (sum > target)
				break;
		}
	}

	return ans;
}

// O(nlogn) O(n)
// Binary Search
public int minOperations3(int[] nums, int x) {

	int len = nums.length, total = 0;
	for (int i = 0; i < len; i++)
		total += nums[i];

	int target = total - x;
	int ans = subArraySum3(nums, target);
	return (ans == -1) ? -1 : len - ans;
}

// O(nlogn) O(n)
// Binary Search
public int subArraySum3(int[] nums, int target) {

	int len = nums.length, ans = -1;
	int[] prefix = new int[len];

	prefix[0] = nums[0];
	for (int i = 1; i < len; i++)
		prefix[i] = prefix[i - 1] + nums[i];

	for (int i = 0; i < len; i++) {
		int sum = target + prefix[i] - nums[i];
		int j = binarySearch(prefix, i, len - 1, sum);
		if (j != -1 && j - i + 1 > ans)
			ans = j - i + 1;
	}

	return ans;
}

// O(nlogn) O(n)
// Binary Search
public int binarySearch(int[] prefix, int lo, int hi, int key) {

	while (lo <= hi) {

		int mid = (lo + hi) / 2;
		if (prefix[mid] == key)
			return mid;
		else if (prefix[mid] > key)
			hi = mid - 1;
		else
			lo = mid + 1;
	}

	return -1;
}

// O(n) O(1)
// Sliding Window
public int minOperations4(int[] nums, int x) {

	int len = nums.length, total = 0;
	for (int i = 0; i < len; i++)
		total += nums[i];

	int target = total - x;
	int ans = subArraySumK4(nums, target);
	return (ans == -1) ? -1 : len - ans;
}

// O(n) O(1)
// Sliding Window
public int subArraySumK4(int[] arr, int K) {

	int len = arr.length, ptr1 = 0, ptr2 = -1, sum = 0, ans = -1;

	while (ptr1 < len) {

		while (ptr1 < len) {
			sum += arr[ptr1];
			if (sum == K) {
				if (ptr1 - ptr2 > ans)
					ans = ptr1 - ptr2;
			}
			if (sum > K) {
				ptr1++;
				break;
			}
			ptr1++;
		}

		while (ptr2 < ptr1 - 1) {
			ptr2++;
			sum -= arr[ptr2];
			if (sum == K) {
				if (ptr1 - ptr2 - 1 > ans)
					ans = ptr1 - ptr2 - 1;
			}
			if (sum < K)
				break;
		}

	}

	return ans;
}

