        for (int j = len - 1, lmt = res > -1 ? len - res : 0; j >= lmt && tot < x; j--) {
            // updating res if there is a match
            if (m.find(x - (tot += nums[j])) != end(m)) {
                res = min(res > -1 ? res : len, m[x - tot] + 1 + len - j);
            }
