class Solution {
public:
    int minOperations(vector<int>& nums, int x) {

        if(nums.size() == 1){
            if(x == nums[0])
                return 1;
            return -1;
        }
        if(nums[0] > x && nums[nums.size() -1] > x)
            return -1;

        vector<long long> presum(nums.size()+1,0);

        presum[0] = 0;
        for(int i=1;i<=nums.size();i++){
            presum[i] = presum[i-1] + nums[i-1];
        }
        int ans = nums.size()+1;

        long long end_sum = 0;
        int end_elements = 0;
        int j = nums.size()-1;
        while(j>=0){
            if(binary_search(presum.begin(),presum.end(),x-end_sum)){
                int id = lower_bound(presum.begin(),presum.end(),x-end_sum) - presum.begin();
                if(id+end_elements <= nums.size()){
                    ans = min(ans,id+end_elements);
                }
            }
            end_elements++;

            end_sum += nums[j--];

        }

        if(ans == nums.size()+1)
            return -1;
        else return ans;

    }
};
```

