var minOperations = function(nums, x) {
    let l = 0,
        r = nums.length-1,
        res = nums.length+1;

    let n = x;

    while (l <= r && n-nums[l] >= 0)
        n -= nums[l++];

    if (n === 0)
        res = l;

    while (l < r && l >= 0) {
        while (n-nums[r] >= 0)
            n -= nums[r--];

        if (n === 0)
            res = Math.min(res, l+nums.length-1-r);

        n += nums[--l];
    }

    return res === nums.length+1 ? -1 : res;
};

