int solve(vector<int>& nums, int target)
{
    // Initializing variables
    long long i = 0, j = 0, sum = 0, ans = INT_MIN, n = nums.size();

    // Using a sliding window approach
    while (j < n)
    {
        sum += nums[j];  // Add the current element to the sum

        if (sum == target) {
            ans = max(ans, j - i + 1);  // If the current sum matches the target, update the answer
        }
        else if (sum > target)
        {
            // If the current sum exceeds the target, move the left pointer (i) and decrease the sum
            while (sum > target)
            {
                sum -= nums[i];
                i++;
            }

            if (sum == target) {
                ans = max(ans, j - i + 1);  // If the updated sum matches the target, update the answer
            }
        }

        j++;  // Move the right pointer (j) to the next element
    }

    return ans;  // Return the maximum length of subarray with sum equal to target
}

int minOperations(vector<int>& nums, int x) {
    // Initializing variables
    long long total_sum = 0, n = nums.size();

    // Calculate the total sum of the elements in nums
    for (auto x : nums)sli
        total_sum += x;

    int target = total_sum - x;  // Calculate the target sum

    if (target < 0)
        return -1;  // If the target is negative, it is not possible to obtain the target sum

    if (target == 0)
        return n;  // If the target is zero, return the total number of elements

    int window = solve(nums, target);  // Find the maximum length of subarray with sum equal to target

    if (window == INT_MIN)
        return -1;  // If no subarray found with the target sum, return -1

    return (n - window);  // Return the minimum number of operations required
}
