class Solution {
public:
    int minOperations(vector<int>& nums, int x) {
        int sum(0);

        unordered_map<int,int> prefixsum;

//      Here I had initalized prefixsum[0]=-1 beacuse to handle extreme cases as of left 0 index there will be no prefix sum in left side of array  
        prefixsum[0]=-1;

        int n(nums.size());

//         Extreme cases if last and first index is greater than x
        if( nums[0]>x and nums[n-1]>x )
            return -1;

        for( int i=0;i<n;i++ ){
            sum+=nums[i];
            prefixsum[sum]=i;
        }

//         if summ is less than x
        if( x>sum )
            return -1;

//         if sum is equal to x then no need to remove
        if( sum==x)
            return n;

        int presum=0;

        sum-=x;

        int longest(0);

        for( int i=0;i<n;i++ ){
            presum+=nums[i];

            if( prefixsum.count(presum-sum) ){

                longest=maxi(longest,i-prefixsum[presum-sum]);
            }
        }

//         if no sum is there just return -1 or return answer as we discussed
        return (longest==0)?-1:n-longest;

    }
};
