        for(int i=0;i<n;i++)
            sum+=nums[i];

        if( x>sum )
            return -1;

        if( x==sum )
            return n;

        int ans=topdown(nums,x,0,n-1,0);

        return ans>=1e6?-1:ans;

    }
};



