```
class Solution {
public:
    int minOperations(vector<int>& nums, int x) {
        int n = nums.size();
        int left = -1, right = n-1, tmp = x, store = INT_MAX;
        while(tmp > 0 && left < n-1)
        {
            left++;
            tmp -= nums[left];
        }
        if(tmp == 0)
        {
            store = left+1;
        }
        while(left < right)
        {
            if(tmp < 0 && left > -1)
            {
                tmp += nums[left];
                left--;
            }
            else if(tmp > 0 && right > -1)
            {
                tmp -= nums[right];
                right--;
            }
            else if(tmp == 0)
            {
                int calc = n-right+left;
                store = min(store,calc);
                if(left > -1)
                {
                    tmp += nums[left];
                    left--;
                }
                if(right > -1)
                {
                    tmp -= nums[right];
                    right--;   
                }
            }
            else
            {
                break;
            }
        }
        if(store == INT_MAX)
            store = -1;
        return store;
    }
};
```

