class Solution {
    Integer[][][] dp;
    public int minOperations(int[] nums, int x) {
        dp = new Integer[nums.length][nums.length][x+1];
        int res =dfs(nums, x, 0, nums.length-1);
        return res==Integer.MAX_VALUE? -1: res;
    }
    public int dfs(int[] nums, int target, int l, int r){
        if(target == 0) return l + nums.length-r-1;
        if(target<0) return Integer.MAX_VALUE;
        if(l>r) return Integer.MAX_VALUE;
        if(dp[l][r][target]!= null) return dp[l][r][target];

        int left = dfs(nums, target-nums[l], l+1, r);
        int right = dfs(nums, target-nums[r], l, r-1);
        dp[l][r][target] = Math.min(left, right);
        return Math.min(left, right);
    }
}
```

