class Solution {
    public int minOperations(int[] nums, int x) {
        int len = nums.length;
        // only left;
        int min = Integer.MAX_VALUE;
        Map<Integer, Integer> map = new HashMap<>(); 
        int[] left_sum = new int[len];
        for (int i = 0; i < len; i++) {
            if (i != 0) {
                left_sum[i] = left_sum[i-1] + nums[i];
            } else {
                left_sum[0] = nums[0];
            }
            if (left_sum[i] == x) {
                min = Math.min(min, i + 1);
            }
            map.put(left_sum[i], i + 1);
        }
        if (left_sum[len-1] < x) return -1;
        int[] right_sum = new int[len];
        for (int i = len - 1; i >= 0; i--) {
            if (i != len - 1) {
                right_sum[i] = right_sum[i+1] + nums[i];
            } else {
                right_sum[i] = nums[len-1];
            }
            if (right_sum[i] == x) {
                min = Math.min(min, len - i);
            }
            if (map.containsKey(x - right_sum[i])) {
                min = Math.min(min, len - i + map.get(x - right_sum[i]));
            }
        }
        return (min == Integer.MAX_VALUE) ? -1 : min;
    }
}
