class Solution {
    public int minOperations(int[] nums, int x) {
        int n = nums.length;
        int[] left = new int[n];
        int[] right = new int[n];

        int sum =0;

        for(int i=0;i<n && x>=sum;i++){
            sum +=nums[i];
            left[i] = sum;
        }
        sum = 0;
        for(int i=n-1;i>=0 && x>=sum;i--){
            sum +=nums[i];
            right[i] = sum;
        }

        //use two sum approach to find matching pair 
        int dist = Integer.MAX_VALUE;
        Map<Integer, Integer> map = new HashMap<>();
        map.put(0,0);
        for(int i=0;i<n;i++){
            if(left[i]<=0) break;
            map.put(left[i],i+1);
            if(x-left[i]==0){
                dist = i+1;break;
            }
        }

        for(int i=n-1;i>=0;i--){
            if(right[i]<0) break;
            if(map.containsKey(x-right[i]) && map.get(x-right[i])< i ){
                dist = Math.min(dist, map.get(x-right[i])+n-i);
            }
        }

        return dist!=Integer.MAX_VALUE?dist:-1;



    }

}
