class Solution {
public:
    int minOperations(vector<int>& nums, int x) {
        //see humko min operation me x ko 0 tk reduce krna hai.
        //so, edge cases- if starting ele and last ele of nums is greater than x then it is impossible
        // to reduce x to zero and if total sum also greater than x then it is impossible

        //general case has three parts
        //1. just take elements from start and search lower bound and prefix will increase the complexity
        //2. just take elements from end and 3. just take element from start and end.

        int n=nums.size()-1;
        //edge case
        if(x<min(nums[0],nums[n]))return -1;

        vector<int>leftr(n+1),rightr(n+1);
        leftr[0]=nums[0],rightr[0]=nums[n];
        for(int i=1;i<=n;i++)leftr[i]=leftr[i-1]+nums[i];
        for(int i=n-1;i>=0;i--)rightr[n-i]=rightr[n-i-1]+nums[i];
        int ans=INT_MAX;
        //ele from start
        int a=lower_bound(leftr.begin(),leftr.end(),x)-leftr.begin();
        //ele from end
        int b=lower_bound(rightr.begin(),rightr.end(),x)-rightr.begin();
        //store min
        if(a!=n+1)if(leftr[a]==x)ans=min(ans,a+1);
        if(b!=n+1)if(rightr[b]==x)ans=min(ans,b+1);

        int j=0;
        //edge case -2
        if(rightr[n]<x)return -1;
        while(j<=n)
        {
            if(x<leftr[j])break;
            else
            {
                //general case-3
                int c=lower_bound(rightr.begin(),rightr.end(),x-leftr[j])-rightr.begin();
                if(c!=n+1)if(leftr[j]+rightr[c]==x)ans=min(c+1+j+1,ans);
            }
            j++;
        }
        return ans;
    }
};
```

