class Solution {
public:
    int minOperations(vector<int>& nums, int x) {

        int n = nums.size();

        // motive is to find maximum length of subarray of sum = (sum of array) - x

        int ArraySum = accumulate(nums.begin(),nums.end(),0);

        int sum = ArraySum - x;

        if(sum<0){
            return -1;
