            else if(ssf+nums[i]>sum){
                ssf+=nums[i];

                while(ssf>sum)
                ssf-=nums[j++];

                if(ssf==sum){
                    max_len = max(max_len,i-j+1);
                }
            }
        }

        if(max_len==-1){
            return -1;
        }
        else{
            return n - max_len;
        }
