class Solution {
public:
    int minOperations(vector<int>& nums, int x) {

        int sum=0;
		// Calculating Total sum of our given Array
        for(int i:nums)
            sum+=i;

		// Substracting X from our total sum to get a target sum for our sub-array
        sum=sum-x;
