        int ans=-1;
		// Finding the length of Subarray whose sum = Target_Sum
        while(high<nums.size()){
            cursum+=nums[high];
            while(cursum>sum){
                cursum-=nums[low++];
            }
            if(cursum==sum)
                ans=max(ans,high-low+1);
            high++;
        }

        if(ans==-1)
            return -1;
        return nums.size()-ans;

    }
};

