public class Solution {
    public int MinOperations(int[] nums, int x) {
        int tar = nums.Sum();
        tar -=x;

        if(tar==0)
        {
            return nums.Length;
        }
        else if (tar<0)     //Sum of the array should greater than our target
        {
            return -1;
        }

        int res = Int32.MinValue;
        int sum = 0;   //sum number use in iteration
        int s = 0;     //s = start

        for(int e = 0;e<nums.Length;e++)  // e = end
        {
            sum += nums[e];
            while (s <= e && sum > tar) {
              sum -= nums[s];
              s++;
            }

            if (sum == tar) {
              res = Math.Max(res, e-s+1);
            }
        }

        return res == Int32.MinValue ? -1 : nums.Length - res;
    }
}
```

