class Solution {
    public int minOperations(int[] nums, int x) {
        int totalSum = 0;
        for(int ele:nums)
        {
            totalSum += ele;
        }

        int target = totalSum - x;
        Map<Integer, Integer> map = new HashMap<>();
        map.put(0,-1);

        int maxLenSubArray = -1;
        int prefixSum = 0;
        for(int i=0;i<nums.length;i++)
        {
            prefixSum += nums[i];
            map.put(prefixSum,i);

            if(map.containsKey(prefixSum - target))
            {
                int currSubArrayLen = i - map.get(prefixSum-target);
                maxLenSubArray = Math.max(currSubArrayLen, maxLenSubArray);
            }
        }
        if(maxLenSubArray==-1)
            return maxLenSubArray;

        return nums.length-maxLenSubArray;
    }
}

