// Runtime: 692 ms, faster than 48.90% of C++ online submissions for Minimum Operations to Reduce X to Zero.
// Memory Usage: 144.7 MB, less than 45.55% of C++ online submissions for Minimum Operations to Reduce X to Zero.
class Solution {
public:
    int minOperations(vector<int>& A, int x) {
        // there are 3 possibilities 
            // 1. we take elements from left side only
            // 2. we take elements from right side only
            // 3. we take elements from both left and right side 

        int ans=INT_MAX,s=0; 
        unordered_map<int,int>m;
        for(int i=0;i<A.size();++i)   // sum with left elements only
        {
            s+=A[i];
            if(s==x)
            {
                ans=i+1;    // because numbers are positive so sum in increasing
                break;
            }
            if(s>x) break;  
            m[s]=i+1;
        }
        s=0;
        for(int i=A.size()-1;i>=0;--i)        // now we see if cases 2 or 3 are possible or not
        {
            s+=A[i];
            if(s==x)                          // sum with right elements only
            {
                ans=min(ans,(int)A.size()-i);
            }
            if(m.count(x-s))                  // sum with both left and right elements
            {
                if(m[x-s]<i)                  // ensuring that indicies dont overlap 
                    ans=min(ans,m[x-s]+(int)A.size()-i);
            }
            if(s>x) break;
        }
        if(ans==INT_MAX) return -1;
        return ans;
    }
};
