//here we can use the concept of maximum length subarray of sum equals to K 
// EXPLANATION: if the sum of our array is lets say Z then if we find the the longest subarray of sum Z-X then we 
// left with sum equals to X with minimum no of elements and that is the required answer :)
class Solution {
public:
    int minOperations(vector<int>& nums, int x) {
        if(nums.size()==0)
        {
            return 0;
        }
        unordered_map<int,int> ma;
        int sum1=0;
        for(int i=0; i<nums.size(); i++)
        {
            sum1+=nums[i];
        }
        if(sum1==x)
        {
            return nums.size();
        }
        int dif=sum1-x;
        int sum=0,ans=INT_MAX;
        for(int i=0; i<nums.size(); i++)
        {
            sum+=nums[i];
            if(sum-dif==0)
            {
                int p=(nums.size()-(i+1));
                ans=min(ans,p);
                //ans=min(1,0);
            }
            else if(ma.find(sum-dif)!=ma.end())
            {
               int p=nums.size()-(i-(ma[sum-dif]));
               ans=min(ans,p);
            }
            else
            {
                ma.insert({sum,i});
            }
        }
        if(ans==INT_MAX)
        {
            return -1;
        }
        else
        {
            return ans;
        }
    }
};

