class Solution {

    public int minOperations(int[] nums, int x) {
        int sum = 0;
        for (int num : nums)
            sum+= num;
        if(sum == x)
            return nums.length;
        int targetsum = sum-x;
        int start = 0;
        int end= 0;
        int maxLen = -1;
        int windowSum =0;
        while (end < nums.length){
            if(nums[end] > targetsum){
                int ind = end+1;
                start = ind;
                end = ind;
                continue;
            }
            //move end by 1
            if(windowSum + nums[end] < targetsum){
                windowSum = windowSum+ nums[end];
                end++;
                continue;
            }
            if(windowSum + nums[end] == targetsum){
                windowSum = windowSum + nums[end];
                maxLen = Math.max(maxLen, end-start+1);
                windowSum = windowSum -nums[start];
                //move end and start by 1
                start++;
                end++;
                continue;
            }
            //advacne start untill windowsum is less than targetsum
            if(windowSum + nums[end] > targetsum){
                windowSum = windowSum + nums[end];
                while (windowSum > targetsum){
                    windowSum = windowSum- nums[start];
                    start++;
                }
                windowSum = windowSum-nums[end];
            }
        }
        if(maxLen == -1)
            return -1;
        return nums.length-maxLen;
    }


}
