        HashMap<Integer,Integer> map = new HashMap<>();
        map.put(0,-1);
        for(int i=0;i<n;i++)
        {
            sum = sum + nums[i];
            map.put(sum,i);
        }

        if(sum < x)
        {
            return -1;
        }

        int target = sum - x;
        int longest = Integer.MIN_VALUE;
        sum = 0;
        for(int i=0;i<n;i++)
        {
            sum = sum + nums[i];
            if(map.containsKey(sum - target))
            {
                longest = Math.max(longest , i - map.get(sum - target));
            }
        }

        return longest == Integer.MIN_VALUE ? -1 : n - longest;
    }
}
