class Solution {
public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) 
    {

        int m = mat.size(), n = mat.front().size(), cs = 5000, res = cs;
        bitset<5000> possible_sum;
        for(int i=0; i<n; ++i) possible_sum[mat[0][i]] = 1;
        for(int i=1; i<m; ++i) {
            bitset<5000> pre = possible_sum, on_that_row;
            for(int j=0; j<n; ++j) {
                on_that_row |= (pre << mat[i][j]);
            }
            possible_sum = on_that_row;
        }
        for(int i=0; i<cs; ++i) {
            if(possible_sum[i]) res = min(res, abs(i-target));
        }
        return res;
    }
};
