class Solution {
public:

    int n,m,goal;

    int dp[71][4905];
    vector<int> v;

    int solve(vector<vector<int>>& mat,int i,int sum)
    {
        if(i>=n) 
        {
            //picking element from each row and taking abs diff of all possible path.
            return abs(sum-goal);
        }

        //since sum>=goal,so from ith row till last we will only pick min element from each bcz          
		//adding other element only increase abs diff as all element are +ve. so add all min             
		//element from ith till last row and take abs diff.
        if(sum>=goal) return abs(sum+v[i]-goal);

        if(dp[i][sum]!=-1) return dp[i][sum]; 

        int ans=INT_MAX;

        for(int j=0;j<m;j++)
        {
            ans=min(ans,solve(mat,i+1,sum+mat[i][j]));
        }

        return dp[i][sum]=ans;
    }


    int minimizeTheDifference(vector<vector<int>>& mat, int target) {

        n=mat.size(),m=mat[0].size();
        goal=target;

        for(auto &x:mat)
        {
            v.push_back(*min_element(begin(x),end(x)));//stores min element of each row
        }

        //v[i] now stores sum of min element of each row from i till last row
        for(int i=n-2;i>=0;i--) v[i]+=v[i+1];

        memset(dp,-1,sizeof(dp));

        return solve(mat,0,0);

    }
};
