public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {

        int m=mat.size();
        int n=mat[0].size();

        vector<vector<int>>dp(m+1,vector<int>(4900+2,-1));
        int sum=0;
        return minDiff(m,n,mat,target,sum,dp);
    }
};

