class Solution {
public:
    int dp[5001][71];
    int mini = INT_MAX;
    int find(vector<vector<int>>& mat,int i,int sum,int tar)
    {
		if(dp[sum][i]!=-1)   return dp[sum][i];
		//Base Case
		//When we consume all the rows , update the min answer till now.
        if(i>=mat.size()){
            mini = min(mini,abs(sum-tar));
            return dp[sum][i] = abs(sum-tar);
        }
		//For early exit:
		//if sum has already exceeded target, then going forward, this difference 
		//is always going to increase only ,as all elements are positive and if the current 
		//difference is already greater than mini, then this can't be our potential answer:
		//So, stop right there:
        if(tar<sum && abs(sum-tar)>mini){
            return dp[sum][i] = 5000;
        }

		//Trying out all the combination selecting one element from each row
        int ans=INT_MAX;
        for(int j=0;j<mat[i].size();j++)
        {
		//since every row is sorted , so if we encounter the same element on a row again , 
		// it will ultimately give the same result as previous one, so it won't be of any use
		//to us, so we skip it
            if(j!=0 && mat[i][j]==mat[i][j-1])  continue;
            ans=min(ans,find(mat,i+1,sum+mat[i][j],tar));
        }
        return dp[sum][i]=ans;
    }
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        memset(dp,-1,sizeof(dp));
		//Make the matrix rowwise sorted
        for(int i=0;i<mat.size();i++){
            sort(mat[i].begin(),mat[i].end());
        }
        return find(mat,0,0,target);

    }
};
