class Solution {
public:
    int prefix[80] = {0};
    int minimizeTheDifference(vector<vector<int>>& mat, int target) 
    {
        vector<vector<int>> vec(mat.size(),vector<int>(800,INT_MAX));
        for(int i = mat.size()-1;i >= 0;i--)
        {
            int a = INT_MAX;
            for(int j = 0;j < mat[0].size();j++)
            a = min(a,mat[i][j]);
            prefix[i] = prefix[i+1]+a;
        }
        return abs(solve(mat,target,0,0,vec));
    }
    int solve(vector<vector<int>>& mat,int target,int index,int sum,vector<vector<int>>& vec)
    {
        int diff = INT_MAX;
        if(index == mat.size())
        return target-sum;
        if(sum >= target)
        return target-sum-prefix[index];
        if(vec[index][sum]!=INT_MAX)
        return vec[index][sum];
        for(int j = 0;j < mat[0].size();j++){
            int a = solve(mat,target,index+1,sum+mat[index][j],vec);
            if(abs(diff) > abs(a))
            diff = a;
        }
        return vec[index][sum] = diff;
    }
};
