class Solution {
    int min=Integer.MAX_VALUE;
    boolean[][] dp;
    public int minimizeTheDifference(int[][] mat, int target) {
        dp = new boolean[mat.length][5000];
        backTrack(0,mat, target, 0);
        return min;
    }

    public void backTrack(int row, int[][] mat, int target, int currSum ) {
        if(row==mat.length) {
            if(Math.abs(target-currSum)<min)
                min = Math.abs(target-currSum);
            return;
        }

        for(int i=0; i<mat[0].length; i++) {
            int sumNow = currSum+mat[row][i];
            if(dp[row][sumNow])
                continue;
            dp[row][sumNow] = true;
            backTrack(row+1, mat, target, sumNow);
        }
    }
}```

