class Solution {
public:

    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        int m = mat.size();
        int n = mat[0].size();
        unordered_set<int> s = {};
        s.insert(0);
        for (int i = 0; i < m; i++) {
            unordered_set<int> small = {};
            int large = INT_MAX;
            for (auto a: s) {
                for (auto b : mat[i]) {
                    int v = a + b;
                    if (v <= target) {
                        small.insert(v);
                    } else {
                        large = min(large, v);
                    }
                }
            }
            s = small;
            if (large != INT_MAX) {
                s.insert(large);
            }
        }

        int res = INT_MAX;
        for (auto a : s) {
            res = min(res, abs(target - a));
        }

        return res;

    }

};

