class Solution {

    int abs;  //global variabls to keep minimum abs value
    int[][] dp; // whenever we arrive at a particular row we need to see if we had previously arrived on this row with same sum.
    public void recFunc(int[][] a, int sum, int row, int target){
        if(row>=a.length){
            abs=Math.min(abs, Math.abs(target-sum));
            return;
        }        
        if(dp[row][sum]==1) // we have arrived this row before with the same sum value, and calculated resultant abs min value. 
            return;
        for(int j=0;j<a[0].length;j++){
            recFunc(a, sum+a[row][j], row+1, target);

        }
        dp[row][sum]=1;  // mark that we arrives at "row" with "sum", and have calucated resultant min abs value. 
    }

    public int minimizeTheDifference(int[][] mat, int target) {
        abs=Integer.MAX_VALUE;
        dp=new int[mat.length][50000];
        recFunc(mat, 0, 0, target);
        return abs;
    }
}

