class Solution {
    public int minimizeTheDifference(int[][] mat, int t) {
        int m= mat.length;
        int[][] dp= new int[m][5001];
        for(int i=0; i<m; i++){
            for(int j=0; j<5001; j++){
                dp[i][j]=-1;
            }
        }
        return solve(mat,dp,0,t,0);
    }

    public int solve(int[][] mat, int[][] dp, int ind, int t, int val){
        if(ind==mat.length){
            return Math.abs(t-val);
        }

        if(dp[ind][val]!=-1){
            return dp[ind][val];
        }

