def minimizeTheDifference(self, mat: List[List[int]], target: int) -> int:
	m = len(mat)
	for i in range(m):
		mat[i].sort()
	q = set([0])
	for i in range(m):
		temp = set()
		for num1 in q:
			for num2 in mat[i]:
				temp.add(num1+num2)
				if(num1+num2 > target):
					break
		q = temp
	q = sorted(list(q))
	i = bisect_right(q, target)
	if(i == len(q)):
		return target - q[-1]
	if(i == 0):
		return q[0] - target
	return min(target-q[i-1], q[i]-target)
