    def minimizeTheDifference(self, mat: List[List[int]], target: int) -> int:
		# initialise set of attainable numbers using the first row
        arr = set(mat[0])

		# from the second row onwards
		# we add every number in arr with every number in the row
        for row in mat[1:]:
            arr1 = set()
            for num1 in row:
                for num2 in arr:
                    arr1.add(num1 + num2)

			# if there are 1 or more number bigger than target, we only need to keep the smallest one
			# because the larger numbers are considered further from target, and will only get even further as we move towards the bottom
            minMax = float('inf')
            for num in arr1:
                if num > target:
                    minMax = min(minMax, num)
            arr = set()
            for num in arr1:
                if num <= minMax:
                    arr.add(num)

		# find the smallest distance between each number in the attainable array and the target
        distance = float('inf')
        for num in arr:
            if abs(target - num) < distance:
                distance = abs(target - num)

        return distance
