class Solution {
public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        bitset<5000> bits(1); 
        for (auto& row : mat) {
            bitset<5000> temp; 
            for (auto& x : row) temp |= (bits << x); 
            bits = temp; 
        }

        for (int x = 0; ; ++x) 
            if (bits[target+x] || (x <= target && bits[target-x])) return x; 
        return -1; 
    }
};
