class Solution {
public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) 
    {
        int m=mat.size(),n=mat[0].size();
        int dp[m][target+2];
        for(int i=0;i<m;i++)
            for(int j=0;j<=target;j++)
                dp[i][j]=-1;
        for(int i=0;i<m;i++)dp[i][target+1]=INT_MAX;
        for(int i=0;i<n;i++)
        {
            if(mat[0][i]<=target)dp[0][mat[0][i]]=mat[0][i];
            else dp[0][target+1]=min(dp[0][target+1],mat[0][i]);      
        }
        for(int i=1;i<m;i++)
            for(int j=0;j<n;j++)
                for(int k=0;k<=target+1;k++)
                {
                    if(dp[i-1][k]==-1||dp[i-1][k]==INT_MAX)continue;
                    if(k+mat[i][j]>target)
                    {
                        if(k<=target)dp[i][target+1]=min(dp[i][target+1],k+mat[i][j]);
                        else dp[i][target+1]=min(dp[i][target+1],dp[i-1][k]+mat[i][j]);
                    }else dp[i][k+mat[i][j]]=k+mat[i][j];
                }
        int ans=INT_MAX;
        for(int i=0;i<=target+1;i++)
            if(dp[m-1][i]!=-1)ans=min(ans,abs(target-dp[m-1][i]));
        return ans;
    }
};
