            HashSet<Integer>newSet=new HashSet();
            HashSet<Integer>row=new HashSet();

            // get every value in the row
            for(int j=0; j<n; j++){
                row.add(mat[i][j]);
            }

            for(int k: set){
                for(int r: row){
                    // get every value so far 
                    newSet.add(r+k);
                }
            }
            set=new HashSet(newSet);
        }

        int min=Integer.MAX_VALUE;

        for(int i: set){
            min=Math.min(min, Math.abs(target-i));
        }

        return min;        
    }
}
