class Solution {
    public int minimizeTheDifference(int[][] mat, int target) {
        int min_sum = 0;
        for (int[] row : mat) {
            int min = Integer.MAX_VALUE;
            for (int num : row) {
                min = Math.min(num, min);
            }
            min_sum += min;
        }
        int threshold = Math.abs(min_sum - target);

        Set<Integer> lastRow = new HashSet<>();
        lastRow.add(target);
        for (int[] row : mat) {
            boolean[] array = new boolean[71];
            for (int num : row) {
                array[num] = true;
            }
            Set<Integer> curRow = new HashSet<>();
            for (int i = 1; i <= 70; i++) {
                if (array[i]) {
                    for (int val : lastRow) {
                        if (val - i < -target) continue;
                        curRow.add(val - i);
                    }
                }
            }
            lastRow = curRow;
        }
        int res = threshold;
        for (int val : lastRow) {
            res = Math.min(res, Math.abs(val));
        }
        return res;
    }
}
