```javascript
/**
 * Collect all the possible sums but keep only one sum that is greater than the target
 */
var minimizeTheDifference = function(mat, target) {
    let possibleSums = new Set(mat[0]);
    for (let row = 1; row < mat.length; row++) {
        const nextPossibleSums = new Set();
        let min = Infinity;
        for (let col = 0; col < mat[row].length; col++) {
            for (const num of possibleSums) {
                const sum = num + mat[row][col];
                // if sum is smaller than the min then it could be the best sum
                if (sum < min) {
                    nextPossibleSums.add(sum);
                    // if the sum is greater than or equal to the target and
                    // less than the min we know the previous min can not be
                    // a better sum so we can delete it from the possible sums
                    if (sum >= target) {
                        nextPossibleSums.delete(min);
                        min = sum;
                    }
                }
            }
        }
        possibleSums = nextPossibleSums;
    }
    for (let i = 0; i < Infinity; i++) {
        if (possibleSums.has(target + i) || possibleSums.has(target - i)) {
            return i;
        }
    }
};
