// DP
class Solution {

    public int minimizeTheDifference(int[][] mat, int target) {
        int m = mat.length;
        int n = mat[0].length;

        int[][] dp = new int[m + 1][4970 + 1]; 

        for (int i = m; i >= 0; i--) {
            for (int j = 4900; j >= 0; j--) {
                if (i == m) {
                    dp[i][j] = Math.abs(j - target);
                    continue;
                }
                int min = 100000000;
                for (int c = 0; c < n; c++) {
                    int o = dp[i + 1][j + mat[i][c]];
                    min = Math.min(min, o);
                }

                dp[i][j] = min;
            }
        }
        return dp[0][0];
    }

}

