class Solution {
    private int minMaxNum = Integer.MAX_VALUE;
    public int minimizeTheDifference(int[][] mat, int target) {

        // Map<Integer, Integer>[] dp = new HashMap[mat.length];
        // for(int i = 0; i < mat.length; i++){
        //     Map<Integer, Integer> temp = new HashMap<>();
        //     dp[i] = temp;
        // }
        int[][] dp = new int[mat.length][5001];
        for(int[] d: dp){
            Arrays.fill(d, -1);
        }

        return helper(mat, 0, 0, 0, target, dp);
    }


    private int helper(int[][] mat, int curr, int prev, int idx, int target, int[][] dp){
        if(idx < mat.length && curr > minMaxNum){
            return Integer.MAX_VALUE;
        }
        if(idx == mat.length){
            if(curr > target){
                minMaxNum = Math.min(minMaxNum, curr);
            }
            return Math.abs(curr - target);
        }
        if(dp[idx][curr] != -1){
            return dp[idx][curr];
        }
        int minDiff = Integer.MAX_VALUE;
        for(int i = 0; i < mat[0].length; i++){
            minDiff = Math.min(helper(mat, curr + mat[idx][i], curr, idx + 1, target, dp), minDiff);
            dp[idx][curr] = minDiff;
        }

        return minDiff;
    }
}
