		}
		//display_hexarray(currentSet);
		memcpy(previousSet, currentSet, SIZE_IN_BITS);
	}
	for(unsigned int currSum=0; currSum < BITS_MASK_SIZE; currSum++){
		// Current Sum is present!
		if(BITTEST(previousSet, currSum)){
			//printf("%i, ", currSum);
			unsigned int dist = abs(target - currSum);
			if(dist < min){
				min = dist;
			}
			if(min == 0) return 0;
		}
	}
	return min;
}
