def minimizeTheDifference(self, mat: List[List[int]], target: int) -> int:
        pool = set(mat[0])
        large = float('inf')
        for i in range(1, len(mat)):
            rowSet=set()
            for j in range(len(mat[0])):
                for ele in pool:
                    tot = ele+mat[i][j]
                    if tot >= target:
                        if tot < large:
                            large = tot
                    else:
                        rowSet.add(ele+mat[i][j])
            pool=rowSet
            rowSet.add(large)
            large = float('inf')
        arr = sorted(pool)
        for i,n in enumerate(arr):
            if n >= target:
                return min(abs(n-target), abs(arr[i-1]-target))
        return target - arr[-1]
