class Solution {
public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        bitset<4900> prev, next;
        int m = mat.size(), n = mat[0].size();
        for (int i = 0; i < n; i++) {
            prev[mat[0][i]] = 1;
        }
        for (int i = 1; i < m; i++) {
            for (int j = 0; j < n; j++) {
                next |= prev << mat[i][j];
            }
            prev = next;
            next.reset();
        }
        int ans = INT_MAX;
        for (int i = 0; i <= 4900; i++) {
            if (prev[i]) {
                ans = min(ans, abs(i - target));
            }
        }
        return ans;
    }
};
