class Solution {
public:
    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        // we can solve this question using dynamic programming and greedy strategy
        // this problem is kinda like knapsack dp with some major modifications
        // the main trick is to handle the edge cases

        int m=mat.size(), n=mat[0].size();

        // precompute the smallest element in each row
        vector<int> row_min(m);

        for(int i=0; i<m; i++) {
            row_min[i] = mat[i][0];

            for(int j=1; j<n; j++) {
                row_min[i] = min(row_min[i], mat[i][j]);
            }
        }

        vector<vector<int>> memo(m, vector<int>(target+1, -1));

        function<int(int,int)> solve = [&](int i, int target) {
            if(i == m) return abs(target);
            if(target < 0) {
                // since the target is less than 0, adding larger elements will only increase the absolute difference
                // hence, for the remaining rows, choose the smallest elements and compute the difference
                // we are greedily choosing the smaller values for the remaining rows....
                int remaining_sum = accumulate(row_min.begin()+i, row_min.end(), 0);
                return abs(target-remaining_sum);
            }            
            if(memo[i][target] != -1) return memo[i][target];

            int result = INT_MAX;

            // try all the elements in the current row as any of them may lead to the optimal solution
            for(int j=0; j<n; j++) {
                result = min(result, solve(i+1, target-mat[i][j]));
            }

            return memo[i][target] = result;
        };

        return solve(0, target);
    }
};
```

