    def minimizeTheDifference(self, mat: List[List[int]], target: int) -> int:
        #there are (no_cols)^(no_rows) ways of choosing elements, 70^70 is too large
        m = len(mat); n = len(mat[0])

        #DP Approach: TC:85% 4000ms
        for i in range(m):
            mat[i] = sorted(mat[i])

        @lru_cache(None)
        def dp(row, target):
            if target < 0:#since target < 0, choose the smallest remaining elements
                return sum([mat[i][0] for i in range(row, m)])
            if row == m:
                return 0

            c_best = inf
            for col in range(n):
                s = dp(row + 1, target - mat[row][col])
                if abs(s + mat[row][col] - target) < abs(c_best - target):
                    c_best = s + mat[row][col]
                    if c_best == target: return c_best #if target is found, no need to search further

            return c_best

        return abs(dp(0, target) - target)


        #Bit set approach
        #TC:100%, 185ms

        #store all possibe sums u can in a mask
        #since max(mat[i][j]) = 70, 70*70=4900 bits which is not that large, so storing in mask is possible

        mask = 1 #mask represents all the prev sums stored,
        #mask = 1, means prev_sum contains 0, mask = 10111 means prev_sum contains 4,2,1,0

        for row in mat:
            next_mask = 0
            for num in row:
                #u can pair all prev sums with num
                next_mask |= (mask << num)
            mask = next_mask
        #start from min deviation of 0, if not found go to 1, if not found go to 2, ans so on....
        for deviation in count():
            for num in (target - deviation, target + deviation):
                if num >= 0 and mask & (1 << num):
                    return deviation
