    int dfs_distance(vector<vector<int>>& mat, int target, int row_index, int acc, map<pair<int, int>, int> &memory) {

        if (row_index == mat.size())
            return abs(acc - target);

        pair<int,int> key = make_pair(row_index, acc);

        if (memory.find(key) != memory.end()) //using the cached value
            return memory[key];

        vector<int> &row = mat[row_index];
        int diff = INT_MAX;
        for (int i = 0, S = row.size(); i < S; ++i) {
            int v = row[i];
            int current_diff = dfs_distance(mat, target, row_index + 1, acc + v, memory);
            diff = min(diff, current_diff);
            if (diff == 0 || acc + v > target)
                break;
        }

        memory[key] = diff;

        return diff;
    }

    int DP(vector<vector<int>>& mat, int target) {

        for (vector<int> &row : mat) {
            sort(row.begin(), row.end());
        }

        int rows = mat.size();
        map<pair<int, int>, int> memory;

        return dfs_distance(mat, target, 0, 0, memory);

    }

    int minimizeTheDifference(vector<vector<int>>& mat, int target) {
        return DP(mat, target);
    }
